import React from "react"; 
import Button from "./Button"; 
import Break from "./Break";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'; 
import Form from 'react-bootstrap/Form';
import "./styles/Newsletter.css";


function Newsletter(){

    const [emailValue, setEmail] = React.useState(''); 
    return(
        <div>

            <Form action="https://gmail.us7.list-manage.com/subscribe/post-json?c=?" method="POST" target="hiddenFrame" noValidate>
            <input type="hidden" name="u" value="199a0e6da31cf6eef4bb76906"/>
             <input type="hidden" name="id" value="bb564e7f6a"/>
                <Row>
                    <Col  md={{ span: 6, offset: 3 }}>
                        <Break text = "Sign up for our newsletter"/>
                        <Form.Group  >
                        {/* <Form.Label>Email</Form.Label> */}
                        <Form.Control id = "email" type="email" placeholder="Enter email"  name = "MERGE0" value = {emailValue}  onChange={ (e)=>{setEmail(e.target.value);} } 
                        autoCapitalize="off" 
                        autoCorrect="off"/>
                        </Form.Group>
                        <div style={{textAlign: "center"}}>
                        <Button text = "Subscribe" type = "submit" name = "submit" id = "mc-embedded-subscribe" onSubmit = {() => {setEmail('');}}/>
                        </div>
                    </Col>


                </Row>

            </Form>
            <iframe name="hiddenFrame" src="about:blank" />

        </div>
    );
}
export default Newsletter; 


